"use strict";

var revealBtn = document.querySelector("[data-reveal]"),
  tabs = document.querySelectorAll(".our-specialists-doctors-show");
revealBtn.addEventListener("click", function () {
  tabs.forEach(function (e) {
    e.classList.toggle("show");
  }), revealBtn.innerHTML = "open" === revealBtn.dataset.reveal ? "Закрыть" : "Загрузить ещё", revealBtn.dataset.reveal = "open" === revealBtn.dataset.reveal ? "close" : "open";
});
var wrapper = document.querySelector(".our-services-wrapper-carousel"),
  carousel = wrapper.querySelector(".our-services-carousel"),
  arrow = wrapper.querySelectorAll(".arrow");
arrow.forEach(function (r) {
  return r.addEventListener("click", function () {
    "right" === r.dataset.direction ? carousel.scrollLeft += 300 : carousel.scrollLeft -= 300;
  });
});